<?php$query = 'SELECT  `title`, `article`, `autor`, `date`          FROM `articles`          ORDER BY `date` DESC          ';$query = $pdo->query($query);$articles = $query->fetchAll(PDO::FETCH_ASSOC);?><ul>    <?php    $i = 1;    foreach($articles as $article) {?>        <li class="list-item">            <?php echo $i.'. '.$article['title'];?>        </li>    <?php $i++; }?></ul>