<?php$page = isset($_GET['page']) ? (int)$_GET['page'] : 1;$perPage = isset($_GET['per-page']) && (int)$_GET['per-page'] <= 30 ? (int)$_GET['per-page'] : 5;$start = ($page > 1) ? ($page - 1 ) * $perPage : 0;$articles = $pdo->prepare("    SELECT SQL_CALC_FOUND_ROWS article_id, title    FROM articles    LIMIT :start, :perPage");$articles->bindParam(':perPage', $perPage, PDO::PARAM_INT);$articles->bindParam(':start', $start, PDO::PARAM_INT);$articles->execute();$articles = $articles->fetchAll(PDO::FETCH_ASSOC);$total = $pdo->query("SELECT FOUND_ROWS() as total");$total = $total->fetch()['total'];$pages = ceil($total / $perPage);?><!DOCTYPE html ><html><head>    <title>PDO подключение и выборка с Data Object</title>    <meta name="description" content="Alternanive article">    <meta name="keywords" content="Alternanive article">    <meta name="author" content="Alternanive article">    <meta name="reply-to" content="Alternanive article">    <meta name="robots" content="index, follow">    <meta name="rating" content="General">    <meta name="copyright" content="Alternanive article">    <meta charset="utf-8">    <link rel="stylesheet" href="../templates/pagination/style.css">    <link rel="stylesheet" href="../templates/pagination/bootstrap-style.css">    <!--<link rel="stylesheet" href="../templates/pagination/bootstrap.css">--></head><body><div class="container">    <div class="articles">        <?php foreach ($articles as $article):?>            <p><span><?php echo $article['article_id']?></span>: <?php echo $article['title']; ?></p>        <?php endforeach; ?>    </div>    <nav>        <ul class="pagination">            <li>                <a href="#" aria-label="Previous">                    <span aria-hidden="true">&laquo;</span>                </a>            </li>            <?php for($x= 1; $x <= $pages; $x++):?>                <li><a href="?page=<?php echo $x;?>&per-page=<?php echo $perPage; ?>" class="<?php if($page === $x){                        echo '\"selected\"'; }?>"><?php echo $x;?></a></li>            <?php endfor; ?>            <li>                <a href="" aria-label="Next">                    <span aria-hidden="true">&raquo;</span>                </a>            </li>        </ul>    </nav></div></body></html>